import { Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';


const Meal = (props) => {
    const { meal: { idMeal, strMeal, strInstructions, strMealThumb } = {} } = props;
    return (

        <div>
            <Col>
                <Card>
                    <Card.Img variant="top" src={strMealThumb} />
                    <Card.Body>
                        <Card.Title>{strMeal}</Card.Title>
                        <Card.Text>
                            {strInstructions.slice(0, 250)}
                        </Card.Text>
                        {/* <button style={{ color: "white", padding: "3px 18px", backgroundColor: "blue", border: "0px", borderRadius: "5px" }}
                            onClick={() => props.handleClick({ props.meal.idMeal })}
                        >Details</button> */}
                    </Card.Body>
                </Card>
            </Col>
        </div>

    );
};

export default Meal;
