import React from 'react';
import { Button, Container, Form, FormControl, Nav, Navbar, NavLink } from 'react-bootstrap';


const Header = (props) => {
    const { handler } = props
    return (
        <>
            <Navbar bg="light" expand="lg" className="" style={{ marginBottom: "30px" }}>
                <Container>
                    <Navbar.Brand href="https://icon-library.com/images/recipe-icon-png/recipe-icon-png-8.jpg">Brand link</Navbar.Brand>
                    <img src="https://icon-library.com/images/recipe-icon-png/recipe-icon-png-8.jpg" alt="" className="img-fluid"
                        style={{
                            width: "5rem"
                        }}
                    />
                    <Navbar.Toggle aria-controls="navbarScroll" />
                    <Navbar.Collapse id="navbarScroll">
                        <Nav
                            className="me-auto my-2 my-lg-0"
                            style={{ maxHeight: '100px' }}
                            navbarScroll
                        >
                            <NavLink href="/home" active className="text-black">Home</NavLink>
                        </Nav>
                        <Form className="d-flex">
                            <FormControl
                                type="search"
                                placeholder="Search"
                                onChange={handler}
                                className="me-2"
                                aria-label="Search"
                            />
                            <Button variant="outline-success">Search</Button>
                        </Form>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </>
    );
};

export default Header;
